    def get_children(self,name):
        children_ids = []
        name_id = self.obtain_id(name)

        for i in self.annotation_id_to_parent_id.items():
            if i[1] == 'nan':
                continue
            elif name_id == int(i[1]):
                children_ids.append(i[0])

        return children_ids
    
    def get_descendents(self,name,option='acr'):
    
        name_id = self.obtain_id(name)
        child_list = self.get_children(name=name_id)
        descendents_container = []
        descendents_container+=child_list

        if len(child_list) == 0:
            raise ValueError(f'No children for {name_id}: {self.id_to_annotation_name[name_id]}!')
        
        final_descendents = self._rescursive_func(descendent_list=descendents_container,child_list=child_list)

        if option == 'id':
            return final_descendents
        if option == 'acr':
            final_descendents_acr = self.list_to_dict(input_list=final_descendents,input_dict=self.id_to_acronym)
            return final_descendents_acr
        if option == 'name':
            final_descendents_name = self.list_to_dict(input_list=final_descendents,input_dict=self.id_to_annotation_name)
            return final_descendents_name            

    def _rescursive_func(self,descendent_list,child_list):
        if len(child_list)==0:
            return descendent_list
        else:
            child_list_ = []
            for i in child_list:
                temp_holder = self.get_children(i)
                descendent_list += temp_holder
                child_list_ += temp_holder
            return self._rescursive_func(descendent_list=descendent_list,child_list = child_list_)

    def obtain_id(self,name):
        is_acr = False
        is_name = False
        is_id = False

        try:
            name = int(float(name))
            for i in self.id_to_acronym.items():
                if name == i[0]:
                    is_id = True
        except:
            for i in self.id_to_acronym.items():
                if name == i[1]:
                    is_acr = True
            for i in self.id_to_annotation_name.items():
                if name == i[1]:
                    is_name = True
        
        if is_id is True:
            return name
        if is_acr is True:
            for i in self.id_to_acronym.items():
                if name == i[1]:
                    return i[0]
        if is_name is True:
            for i in self.id_to_annotation_name.items():
                if name == i[1]:
                    return i[0]
        else:
            raise ValueError(f'The input name: {name} is not valid; ensure that the ID/Acronym/Annotation is from the Allen Mouse CCF (Common Coordinate Framework)')
        
    def list_to_dict(self,input_list,input_dict):
        final_list = [input_dict[i] for i in input_list]
        return final_list



