    def create_merged(self,inner_path,outer_path,merged_path,outer_region):
        cell_data_inner = pd.read_csv(inner_path)
        acr_cell_data_inner =  list(set(cell_data_inner['acr'].to_list()))

        acr_inner = self.get_descendents(outer_region)

        for i in acr_inner:
            if i in acr_cell_data_inner:
                acr_cell_data_inner.remove(i)
        cell_data_outer = pd.read_csv(outer_path)
        for i in acr_cell_data_inner:
            cell_data_inner = cell_data_inner.drop(cell_data_inner[cell_data_inner.acr == i].index)
        for i in acr_inner:
            try:
                cell_data_outer = cell_data_outer.drop(cell_data_outer[cell_data_outer.acr==i])
            except:
                continue

        result = pd.concat([cell_data_inner,cell_data_outer]).reset_index(drop=True)

        result.to_csv(merged_path,index=False)
        
        print('Output Complete!')
            
    def replace_descendents(self,input_path,output_path,region):
        id_ = self.obtain_id(region)
        acr = self.id_to_acronym[id_]
        cell_data = pd.read_csv(input_path)
        descendents = self.get_descendents(region)

        for i in descendents:
            cell_data.loc[cell_data.acr == i,'acr'] = acr
        cell_data.to_csv(output_path,index=False)

        print('Output Complete!')
        


